import java.util.Scanner;

//calculate the investment with the investment amount, interest rate, and years invested

public class calculateInvestment {
  public void calculateInvestmentMethod(){

    //Get user input
    //Get amount invested 
    System.out.println("Enter the amount invested (exclude $): ");
    Scanner input = new Scanner(System.in);
    double investmentAmount = input.nextDouble();

    //Get the annual interest rate and turn it into monthly interest, then convert to decimal
    System.out.println("Enter the annual interest rate: ");
    double annualInterestRate = input.nextDouble();
    double monthlyInterestRate = annualInterestRate/12/100;

    //Get the number of years invested
    System.out.println("Enter the number of years invested: ");
    double numYears = input.nextDouble();

    //calculate the investment value 
    double futureInvestmentValue = investmentAmount * (int)Math.pow(1+ monthlyInterestRate, numYears*12);

    //return futureInvestmentValue;
    System.out.println("Your future investment is worth: $" + futureInvestmentValue);

    //Scanner.close();

  }
}

